mapper.select:

SELECT * FROM (
      SELECT ROWNUM AS RNUM, A.* FROM (
               SELECT
                     *
                 FROM
                     ms_product 

     ) A WHERE ROWNUM <= #{pageNum} * #{amount}
 ) WHERE RNUM > (#{pageNum}-1) * #{amount}


SELECT * FROM (
      SELECT ROWNUM AS RNUM, A.* FROM (
               SELECT
                     *
                 FROM
                     ms_product 
                   where product_number like 'R%'
     ) A WHERE ROWNUM <= #{pageNum} * #{amount}
 ) WHERE RNUM > (#{pageNum}-1) * #{amount}


